import './App.css';
import { memo, useState } from 'react';
import {mapa} from "./ArregloMapa"

function App() {

  const [memoria,setMemoria]=useState(mapa);
  const [porBloque,setPorBloque]=useState(false);

  return (
    <div>

      {/*Memoria*/}
      <div className='container'>
        <h1>Memoria</h1>
        <table class="table table-bordered">
          <thead>
            <tr>
            {memoria.map((bit)=>(
              <th scope="col">{bit.posicion}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            <tr>
            {memoria.map((bit)=>(
              <th scope="col">{bit.contenido}</th>
              ))}
            </tr>
          </tbody>
        </table>
        {/*------Formulario*/}
        <h1>Formulario</h1>
        <div className='container'>
          <select class="form-select" aria-label="Default select example">
            <option selected>--Selecione--</option>
            <option value="byte">Por Byte</option>
            <option value="bloque">Por Bloque</option>
          </select>
        </div>
      </div>

    </div>
  );
}

export default App;
