import './App.css';
import { memo, useState } from 'react';
import {mapa} from "./ArregloMapa"

function App() {

  const [memoria,setMemoria]=useState(mapa);
  const [mode,setMode]=useState("");

  console.log(mode);
  return (
    <div>

      {/*Memoria*/}
      <div className='container'>
        <h1>Memoria</h1>
        <table className="table table-bordered">
          <thead>
            <tr>
            {memoria.map((bit,i)=>(
              <th key ={i} scope="col">{bit.posicion}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            <tr>
            {memoria.map((bit,i)=>(
              <th key ={i} scope="col">{bit.contenido}</th>
              ))}
            </tr>
          </tbody>
        </table>
        {/*------Formulario*/}
        <h1>Formulario</h1>
        <div className='container'> 
          <select className="form-select" defaultValue=""  onChange={(e)=>{setMode(e.target.value)}}>
            <option value="" disabled>--Selecione--</option>
            <option value="byte" >Por Byte</option>
            <option value="bloque">Por Bloque</option>
          </select>
          {mode==="byte"? 
          /*por byte*/
          <div><h2>{mode}</h2></div>

          /*por Bloque*/
          :<div><h2>Por {mode}</h2></div>}
        </div>
      </div>

    </div>
  );
}

export default App;
