import './App.css';
import { memo, useState } from 'react';
import {mapa} from "./ArregloMapa"

function App() {

  const [memoria,setMemoria]=useState(mapa);
  const [porBloque,setPorBloque]=useState(false);

  return (
    <div>
      <h1>Memoria</h1>
      {/*Memoria*/}
      <div className='container'>
        <table class="table table-bordered">
          <thead>
            <tr>
            {memoria.map((bit)=>(
              <th scope="col">{bit.posicion}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            <tr>
            {memoria.map((bit)=>(
              <th scope="col">{bit.contenido}</th>
              ))}
            </tr>
          </tbody>
        </table>
        {/*------Formulario*/}
      </div>

    </div>
  );
}

export default App;
