import React, { useState } from 'react'
import app from '../Theme/app.css'
const Raiz = ({nUsuario,arregloUsuarios,setArregloUsuarios}) => {
  console.log("Raiz, arregloUsuarios",arregloUsuarios);
  console.log('tamano ficheros: ', arregloUsuarios['arregloFicheros'].length);
  const [posicionFocus,setPosicionFocus]=useState(0);

  const bajarCarpeta=()=>{
    if(posicionFocus<arregloUsuarios['arregloFicheros'].length){
      setPosicionFocus(posicionFocus+1)
    }
    console.log("Estamos en esta posicion",posicionFocus)
  }

  return (
    <div className='container'>
        <h1>Paht: {arregloUsuarios['arregloFicheros'][posicionFocus].nombre}</h1>
        <hr></hr>
        <div className='container'>
            <div className='container' style={{justifyContent:'space-between',display:'flex'}}>
                <input type='button' className='btn1' onClick={()=>bajarCarpeta()}/>
                <form className="d-flex">
                  <input className="form-control me-2" type="search"/>
                  <button className="btn btn-outline-success" type="submit">Search</button>
                </form>
                <input type='button' className='btn2'/>    
            </div>
            <hr/>
            <div className='container'>
              {arregloUsuarios['arregloFicheros'].map((carpeta,index)=>{
                return(
                  <button key={index}>{carpeta.nombre}</button>
                )
              })}
            </div>
            <hr/>

        </div>
    </div>
  ) 
}

export default Raiz
