import './App.css';
import { useState,useEffect} from 'react';
import {mapa} from "./ArregloMapa"

function App() {
  const [memoria,setMemoria]=useState(mapa);
  const [mode,setMode]=useState("");
  const [nBloques,setNBloques]=useState(0);
  const [espacios,setEspacios]=useState([]);
  const [proceso,setProceso]=useState({
    nombre:"",
    tamanio:0
  });
  const [listaArchivos, setListaArchivos]=useState([]);
  const [tamanioXbloque,setTamanioXbloque]=useState(0);


  useEffect(() => {
    buscaEspacios();
    if(nBloques>0){
      setTamanioXbloque(Math.ceil(40/nBloques));
      let className='table-primary';
    }
  },[memoria,nBloques])


  //console.log(mode);
  // console.log("Esta es tu memoria: ", memoria);
  // console.log("Este es tu lista de procesos actual: ",listaArchivos);
  // console.log("Estos son tus espacios disponibles",espacios);
  // console.log(espacios.length);
  console.log("El tamanio por bloque sera: ",tamanioXbloque);

  const agregaProceso=()=>{
    buscaEspacios()
    llenaMemoriaPorByte();
    setListaArchivos([...listaArchivos,proceso]);
    //console.log("se ha agregado un proceso con exito");  
    setProceso({nombre:"",tamanio:""})
  }
  const llenaMemoriaPorByte=()=>{
      const arregloAux=memoria.map((bit,index)=>{
        for(let i=0;i<proceso.tamanio;i++){
            if(espacios[i]===index){
              return{
                ...bit,
                contenido:proceso.nombre
              }
            }
        }
      return bit;
    });
      setMemoria(arregloAux);
  }
  const buscaEspacios=()=>{
    const arregloAux=memoria.map((bit,index)=>{
        if(bit.contenido===""){
          return index
        }  
    });
    const arregloAux2=arregloAux.filter((espacio)=>espacio!==undefined);
    setEspacios(arregloAux2);
  }
  const eliminaArchivo=(archivo)=>{
    //limpiamos la memoria de nuestro espacio
    //console.log("este es el archivo a eliminar",archivo);
    const arregloAux=memoria.map((bit,index)=>{
        if(archivo===bit.contenido){
            //console.log("El archivo se encontro en las siguientes posiciones,:",index);
            return{
                ...bit,
                contenido:""
            }
        }
        return bit;
    });
    //Eliminamos el proceso de nuestro registro de procesos
    const arregloAux2=listaArchivos.filter((p)=>{
       return p.nombre!==archivo;
    });
    //seteamos estados
  setListaArchivos(arregloAux2);
  setMemoria(arregloAux);
}

  return (
    <div>

      {/*Memoria*/}
      <div className='container'>
        <h1>Memoria</h1>
        <table className="table table-bordered">
          <thead>
            <tr>
            {memoria.map((bit,i)=>(
              <th key ={i} scope="col" className={className}>{bit.posicion}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            <tr>
            {memoria.map((bit,i)=>(
              <th key ={i} scope="col">{bit.contenido}</th>
              ))}
            </tr>
          </tbody>
        </table>
        {/*------Formulario*/}

        <div className='container'> 
          <select className="form-select" defaultValue=""  onChange={(e)=>{setMode(e.target.value)}}>
            <option value="" disabled>--Selecione--</option>
            <option value="byte" >Por Byte</option>
            <option value="bloque">Por Bloque</option>
          </select>
          <div className='container'>
            {mode==="byte"? 
            /*por byte*/
              <div className=''>
                <h1>Modo: {mode}</h1>
                <h3>Crea un archivo</h3>
                <br/>
                <form>
                  <label>Nombre del archivo </label>
                  <input 
                    type="text"
                    value={proceso.nombre}
                    onChange={(e)=>{setProceso({...proceso,nombre:e.target.value})}}
                  />
                  <br/>
                  <br/>
                  <label>Tamanio del archivo </label>
                  <input 
                    type="number"
                    value={proceso.tamanio}
                    onChange={(e)=>{setProceso({...proceso,tamanio:parseInt(e.target.value)})}}
                    />
                    <br/>
                    <br/>
                    <button type="button" className="btn btn-primary" onClick={agregaProceso} >Crear Archivo</button>
                </form>
                <br/>
                <h2>Lista de Archivos</h2>
                <div>
                  <ul className='d-flex'>
                  {listaArchivos.map((archi)=>(
                    <div className="card border-success m-1">
                      <div className="card-header bg-transparent border-success">
                        <h5>{archi.nombre}</h5>
                      </div>
                      <div className="card-body text-success">
                        <h5 className="card-title">{archi.tamanio}</h5>
                      </div>
                      <div className="card-footer bg-transparent border-success">
                          <button type="button" 
                            className="btn btn-danger" 
                            onClick={()=>{eliminaArchivo(archi.nombre)}}
                            >x
                          </button>
                      </div>    
                    </div>
                  ))
                  }
                  </ul>
                </div>
              </div>
            /*por Bloque*/
            :<div>
                <h2>Modo: {mode}</h2>
                  {nBloques===0 && mode==="bloque" ?  
                  <div>
                    <h2>Ingresa un numero de bloques</h2>
                    <select className="form-select" defaultValue=""  onChange={(e)=>{setNBloques(parseInt(e.target.value))}}>
                      <option value="" disabled>--Selecione--</option>
                      <option value="2" >2</option>
                      <option value="3" >3</option>
                      <option value="4" >4</option>
                      <option value="5" >5</option>
                      <option value="6" >6</option>
                      <option value="7" >7</option>
                      <option value="8" >8</option>
