import './App.css';
import { useState,useEffect} from 'react';
import {mapa} from "./ArregloMapa"

function App() {
  const [memoria,setMemoria]=useState(mapa);
  const [mode,setMode]=useState("");
  const [nBloques,setNBloques]=useState(0);
  const [proceso,setProceso]=useState({
    nombre:"",
    tamanio:0
  });
  const [listaProcesos, setListaProcesos]=useState([]);
  var espacios=[];

  useEffect(() => {
    buscaEspacios(espacios);
  },[])


  console.log(mode);
  console.log("Este es tu proceso actual: ",proceso);
  console.log("Este es tu lista de procesos actual: ",listaProcesos);
  console.log("Estos son tus espacios disponibles",espacios);

  const agregaProceso=()=>{
    llenaMemoriaPorByte();
    setListaProcesos([...listaProcesos,proceso]);
    console.log("se ha agregado un proceso con exito");  
    setProceso({nombre:"",tamanio:""})
  }
  const llenaMemoriaPorByte=()=>{
    if(espacios.length>0){
      const arregloAux=memoria.map((bit,index)=>{
        for(let i=0;i<proceso.tamanio;i++){
            if(espacios[i]===index){
                return{
                    ...bit,
                    contenido:proceso.nombre
                }
            }
        }
        return bit;
    });
      setMemoria(arregloAux);
    }
  }
  function buscaEspacios(espacios){
    memoria.map((bit,index)=>{
        if(bit.contenido===""){
            espacios.push(index);   
        }
    });
  }

  return (
    <div>

      {/*Memoria*/}
      <div className='container'>
        <h1>Memoria</h1>
        <table className="table table-bordered">
          <thead>
            <tr>
            {memoria.map((bit,i)=>(
              <th key ={i} scope="col">{bit.posicion}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            <tr>
            {memoria.map((bit,i)=>(
              <th key ={i} scope="col">{bit.contenido}</th>
              ))}
            </tr>
          </tbody>
        </table>
        {/*------Formulario*/}
        <h1>Formulario</h1>
        <div className='container'> 
          <select className="form-select" defaultValue=""  onChange={(e)=>{setMode(e.target.value)}}>
            <option value="" disabled>--Selecione--</option>
            <option value="byte" >Por Byte</option>
            <option value="bloque">Por Bloque</option>
          </select>
          <div className='container'>
            {mode==="byte"? 
            /*por byte*/
              <div>
                <h3>Modo: {mode}</h3>
                <h3>Crea un archivo</h3>
                <br/>
                <form>
                  <label>Nombre del archivo </label>
                  <input 
                    type="text"
                    value={proceso.nombre}
                    onChange={(e)=>{setProceso({...proceso,nombre:e.target.value})}}
                  />
                  <br/>
                  <br/>
                  <label>Tamanio del archivo </label>
                  <input 
                    type="number"
                    value={proceso.tamanio}
                    onChange={(e)=>{setProceso({...proceso,tamanio:parseInt(e.target.value)})}}
                    />
                    <br/>
                    <br/>
                    <button type="button" className="btn btn-primary" onClick={agregaProceso} >Crear Proceso</button>
                </form>
              </div>
            /*por Bloque*/
            :<div>
                <h2>Modo: {mode}</h2>
            </div>
            }
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
